package com.dkte;

public class Artist {
	int artist_id;
	String artist_name;
	
	public Artist() {
		// TODO Auto-generated constructor stub
	}

	public Artist(int artist_id, String artist_name) {
		super();
		this.artist_id = artist_id;
		this.artist_name = artist_name;
	}

	public int getArtist_id() {
		return artist_id;
	}

	
	public String getArtist_name() {
		return artist_name;
	}

	
	

}
